var fs = require("fs"),
	url = require("url"),
	formidable = require('formidable'),
	mime = require('mime');

function start(response) {
    console.log("Request handler 'start' was called.");

	fs.readFile("/home/raspechatat/sborkaprint.com/http/index.html", function(error, file) {
		if(error) {
			response.writeHead(500, {"Content-Type": "text/plain"});
			response.write(error + "\n");
			response.end();
		} else {
			response.writeHead(200, {"Content-Type": "text/html"});
			response.write(file);
			response.end();
		}
	});

}

function link(response, request) {

	var pathname = url.parse(request.url).pathname;
	console.log("Request handler " + pathname + " was called.");
	fs.readFile("/home/raspechatat/sborkaprint.com/http" + pathname , function(error, file) {
		if(error) {
			response.writeHead(500, {"Content-Type": "text/plain"});
			response.write(error + "\n");
			response.end();
		} else {
			response.writeHead(200, {"Content-Type": mime.lookup(pathname)});
			response.write(file);
			response.end();
		}
	});

}

function upload(response, request) {
    console.log("Request handler 'upload' was called.");
	var form = new formidable.IncomingForm();
	console.log("about to parse");
	form.parse(request, function(error, fields, files) {
		console.log("parsing done");

		/* Возможна ошибка в Windows: попытка переименования уже существующего файла */
		fs.rename(files.upload.path, "/tmp/test.jpg", function(err) {
			console.log(err);
			if (err) {
				fs.unlink("/tmp/test.jpg");
				fs.rename(files.upload.path, "/tmp/test.jpg");
			}
		});
		response.writeHead(200, {"Content-Type": "text/html"});
		response.write("received image:<br/>");
		response.write("<img src='/show' />");
		response.end();
	});
}

exports.start = start;
exports.link = link;
exports.upload = upload;
